const validator = require('validator')

const wrapValidator = require('./wrap-validator')

exports.validators = {
  validators: {}
}

Object.keys(validator).forEach(key => {
  if (key.startsWith('is')) {
    let name = key.replace(/^is/)
    name = name[0].toLowerCase() + name.substring(1)

    exports.validators[name] = wrapValidator(validator[key])
  }
})
